lor: black;\r\n	\r\n	@child-header-image:url(\'/wp-content/themes/classicthunderlimos/images/classic-thunder-limousine-iowa-luxury-transport-coach.png\');\r\n\r\n/* MIXINS */\r\n\r\n	.pete-gradient (@startColor, @endColor) {\r\n			background-color: @startColor;\r\n			background: -webkit-gradient(linear, left top, left bottom, from(@startColor), to(@endColor));\r\n			background:  -webkit-linear-gradient(top, @startColor, @endColor);\r\n			background:   -moz-linear-gradient(top, @startColor, @endColor);\r\n			background:    -ms-linear-gradient(top, @startColor, @endColor);\r\n			background:     -o-linear-gradient(top, @startColor, @endColor);\r\n			background:      linear-gradient(top, @startColor, @endColor);\r\n	}\r\n\r\n	.make-a-reservation-button {\r\n		.pete-gradient(@child-color-grey-lite,@child-color-grey);\r\n		border-radius: 5px;\r\n		padding: 10px 7.5px;\r\n		color: black;\r\n		text-decoration: none;\r\n		text-transform: uppercase;\r\n	\r\n		&:visited { color: black; }\r\n	\r\n		&:hover {\r\n			.pete-gradient(@child-color-grey,@child-color-grey-lite);\r\n		}\r\n	}\r\n\r\n	.comment-content a:visited, .entry-content a:visited { color: black; }\r\n\r\n\r\n/* GENERAL */\r\n	body { \r\n		background-color: @child-color-black-lite;\r\n		font-family: Roboto, Arial, sans-serif;\r\n	}\r\n\r\n	#page { \r\n		background-color: @child-background-color;\r\n		width: 100%;\r\n		padding: 0;\r\n		margin: 0;\r\n		max-width: none;\r\n		border-bottom: 2px solid #bcbdc0;\r\n	}\r\n\r\n	#masthead,\r\n	#main {\r\n		max-width: 960px;\r\n		width: 100%;\r\n		margin: auto;\r\n	}\r\n\r\n	hgroup {\r\n		margin-top: 10px;\r\n		background-image: @child-header-image;\r\n		background-repeat: no-repeat;\r\n		background-size: 100% auto;\r\n	}\r\n\r\n	@media screen and (min-width: 600px) {\r\n		hgroup {	min-height: 120px; }\r\n	}\r\n\r\n	#primary, #secondary  { margin-top: 0; }\r\n	\r\n/* SHELL */\r\n\r\n	/* HEADER */\r\n	\r\n		.site-header h1 a,\r\n		.site-header h1 a:hover,\r\n		.site-header h2 { color: transparent; }\r\n\r\n		.post-banner-info {\r\n			margin: 25px;\r\n			text-align: center;\r\n			color: white;\r\n			font-size: 2rem;\r\n		}\r\n\r\n		.header-reservation-holder {\r\n			text-align: right;\r\n		}\r\n	\r\n	/* NAVIGATION */\r\n	\r\n		.main-navigation {\r\n			margin: 35px 0 0;\r\n	\r\n			div.nav-menu>ul,\r\n			ul.nav-menu {\r\n					border: none;\r\n			}\r\n		\r\n			li {\r\n				border-right: 2px solid white;\r\n				margin: 0 1rem 0 0;\r\n				padding: 0 1rem 0 0;\r\n				text-align: center;\r\n			\r\n				&:first-of-type {\r\n					text-align: left;\r\n				}\r\n				\r\n				&:last-of-type {\r\n					border-right: none;\r\n					margin: 0;\r\n					padding: 0;\r\n					text-align: right;\r\n				}\r\n			\r\n				a { color: white; }\r\n			\r\n				a:hover,\r\n				&.current_page_item a,\r\n				&.current_page_parent a {\r\n					color: @child-color-red;\r\n					font-weight: normal;\r\n				}\r\n	\r\n				&.current_page_item a { font-weight: normal; }\r\n			\r\n				a,\r\n				a:hover {\r\n					font-size: 1.25rem;\r\n					line-height: 1.5rem;\r\n				}\r\n	\r\n				&.menu-item-has-children:hover > a:before {\r\n							display: block;\r\n						  position: absolute;\r\n							top: 21px;\r\n							left: 40%;\r\n							margin-left: 0;\r\n							content: \'\';\r\n							width: 0;\r\n							height: 0;\r\n							border-bottom: solid 10px @child-color-red;\r\n							border-left: solid 10px transparent;\r\n							border-right: solid 10px transparent;\r\n							z-index: 999999;\r\n							display: none;\r\n				}\r\n	\r\n				.sub-menu {\r\n					margin-left: -13px;\r\n					margin-top: 10px;\r\n		\r\n					li {\r\n						text-align: center;\r\n						margin: 0;\r\n						padding: 0;\r\n						border-right: none;\r\n						&:first-of-type,\r\n						&:last-of-type { text-align: center; }\r\n			\r\n						a, a:hover { \r\n							background-color: @child-color-red;\r\n							color: white;\r\n							font-size: 1rem;\r\n							line-height: 1rem;\r\n						}\r\n			\r\n						a:hover { \r\n							background-color: white;\r\n							color: @child-color-red;\r\n						}\r\n					}\r\n				}\r\n			}\r\n		}\r\n\r\n		.social-media-navigation {\r\n			ul { }\r\n			\r\n			li {\r\n				display: inline-block;\r\n				height: 50px;\r\n				width: 50px;\r\n				vertical-align: top;\r\n				background-size: 100%;\r\n		\r\n				&:nth-of-type(1) { background-image: url(\'/wp-content/uploads/2015/04/ctl-limos-social-icon.png\'); }\r\n				&:nth-of-type(2) { background-image: url(\'/wp-content/uploads/2015/04/ctl-knot-social-icon.png\'); }\r\n				&:nth-of-type(3) { background-image: url(\'/wp-content/uploads/2015/04/ctl-rose-social-icon.png\'); }\r\n				&:nth-of-type(4) { background-image: url(\'/wp-content/uploads/2015/04/ctl-facebook-social-icon.png\'); }\r\n				&:nth-of-type(5) { background-image: url(\'/wp-content/uploads/2015/04/ctl-twitter-social-icon.png\'); }\r\n				&:nth-of-type(6) { background-image: url(\'/wp-content/uploads/2015/04/ctl-flowers-social-icon.png\'); }\r\n				\r\n				a, a:hover { color: transparent; }\r\n			}\r\n		}\r\n\r\n	/* FOOTER */\r\n	\r\n		footer[role=contentinfo] {\r\n			background-color:	@child-color-black-lite;\r\n			color: white;\r\n			border-top: none;\r\n			margin-top: 0;\r\n			font-size: 1.5rem;\r\n			font-weight: 300;\r\n	\r\n			.site-info {\r\n				max-width: 960px;\r\n				width: 100%;\r\n				margin: auto;\r\n			}\r\n	\r\n			.footer-contact-info {\r\n				a {\r\n					font-size: 1.25rem;\r\n					color: white;\r\n					text-decoration:none;\r\n				}\r\n			}\r\n		}\r\n\r\n	/* SIDEBAR */\r\n		\r\n		.widget-area {\r\n	\r\n			.widget .widget-title {\r\n				.pete-gradient(@child-color-grey-lite,@child-color-grey);\r\n				color: black;\r\n				text-transform: uppercase;\r\n				height: 50px;\r\n				max-height: 50px;\r\n				padding: 5px;\r\n				font-weight: 300;\r\n				font-size: 16px;\r\n				margin-bottom: 0;\r\n				line-height: 25px;\r\n			}\r\n	\r\n			.sidebar-info-widget {\r\n				  background-color: #b2b3b6;\r\n					margin: 15px 0;\r\n					padding: 30px 20px;\r\n					font-size: 1.1rem;\r\n					line-height: 2.5rem;\r\n		\r\n				span { color:@child-color-red; }\r\n			}\r\n	\r\n			.discount-section {\r\n				background-color: @child-color-red;\r\n				color: white;\r\n				padding: 5px 10px;\r\n				margin: 10px 0;\r\n				height: 80px;\r\n		\r\n					span { \r\n						font-size: 3rem;\r\n						border-right: 1px solid white;\r\n						padding-right: 10px;\r\n						margin-right: 10px;\r\n						display:inline-block;\r\n					}\r\n		\r\n					p {\r\n						display:inline-block;\r\n						max-width: 50%;\r\n						text-transform: uppercase;\r\n					}\r\n			}\r\n		}\r\n\r\n	/*	SOLILOQUY SLIDER STYLES	*/\r\n\r\n		#soliloquy-container-195 {\r\n			max-width: none !important;\r\n			max-height: none !important;\r\n			border-top: 1px solid @child-color-red;\r\n			border-bottom: 1px solid @child-color-red;\r\n		}\r\n		\r\n		.soliloquy-container .soliloquy-image {\r\n			width: 100%;\r\n		}\r\n\r\n\r\n	/*	SHARED PAGE STYLES	*/\r\n		@media screen and (min-width: 600px) {\r\n				.site-content {	width: 72% }\r\n		}\r\n		\r\n		.site-content article {\r\n			border-bottom: none;\r\n			margin-bottom: 0;\r\n			padding-bottom: 0;\r\n		}\r\n\r\n		.entry-header {\r\n			margin-bottom: 15px;\r\n	\r\n			.entry-title {\r\n				.pete-gradient(@child-color-grey-lite,@child-color-grey);\r\n				color: black;\r\n				text-transform: uppercase;\r\n				padding: 5px 10px;\r\n				height: 50px;\r\n				max-height: 50px;\r\n				line-height: 50px;\r\n			}\r\n		}\r\n\r\n		.entry-content {\r\n			background-color: @child-color-grey;\r\n			padding: 20px;\r\n			color: black;\r\n			min-height: 365px;\r\n			\r\n			h3 {\r\n			  font-size: 18px;\r\n  			font-weight: 400;	\r\n				margin-bottom: 0;\r\n				\r\n				&:first-of-type { margin-top: 0; }\r\n			}\r\n		}','safecss','Classic Thunder Limo - LowerMedia Custom Theme','publish','closed','open','','safecss','','','2015-04-11 18:26:07','2015-04-11 18:26:07','.make-a-reservation-button{background-color:#f5f5f5;background:-webkit-gradient(l